// PDF Decryption using pdf-lib
// Receives base64 PDF + password, returns unlocked base64 PDF

const { PDFDocument } = require('pdf-lib');

const inputData = $input.first().json;
const { pdfBase64, password } = inputData;

if (!pdfBase64) {
  return [{ json: { success: false, error: 'No PDF data provided' } }];
}

if (!password) {
  return [{ json: { success: false, error: 'No password provided' } }];
}

try {
  // Decode base64 to bytes
  const pdfBytes = Buffer.from(pdfBase64, 'base64');

  // Load PDF with password
  const pdfDoc = await PDFDocument.load(pdfBytes, {
    password: password,
    ignoreEncryption: false,
  });

  // Save without encryption (removes password protection)
  const unlockedBytes = await pdfDoc.save({
    useObjectStreams: false,
  });

  // Convert back to base64
  const unlockedBase64 = Buffer.from(unlockedBytes).toString('base64');

  return [{
    json: {
      success: true,
      unlockedPdfBase64: unlockedBase64,
      pageCount: pdfDoc.getPageCount(),
      message: 'PDF decrypted successfully'
    }
  }];

} catch (error) {
  // Handle specific pdf-lib errors
  const errorMessage = error.message || 'Unknown error';

  if (errorMessage.includes('password') || errorMessage.includes('decrypt')) {
    return [{
      json: {
        success: false,
        error: 'WRONG_PASSWORD',
        message: 'The password provided is incorrect'
      }
    }];
  }

  if (errorMessage.includes('not encrypted')) {
    return [{
      json: {
        success: false,
        error: 'NOT_ENCRYPTED',
        message: 'This PDF is not password-protected'
      }
    }];
  }

  return [{
    json: {
      success: false,
      error: 'DECRYPT_FAILED',
      message: errorMessage
    }
  }];
}
